
-- init.sql
-- This script runs on database startup

-- Check if the database exists, and create it if it does not
IF NOT EXISTS (SELECT * FROM sys.databases WHERE name = 'PixelPainter')
BEGIN
    CREATE DATABASE PixelPainter;
END
GO

USE PixelPainter;
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Artist')
BEGIN
    CREATE TABLE Artist (
        Id INT IDENTITY(1,1) NOT NULL,
        SubId VARCHAR(21) NOT NULL,
        [Name] VARCHAR(20) NOT NULL,
        Email VARCHAR(40),
        IsAdmin BIT DEFAULT 0, -- this is a bool, 0 = 0 1 = 1
        CreationDate DATETIME DEFAULT GETDATE(),
        PRIMARY KEY (Id)
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Art')
BEGIN
    CREATE TABLE Art (
        Id INT IDENTITY(1,1) NOT NULL,
        ArtistId INT,
        Title VARCHAR(255),
        IsPublic BIT DEFAULT 0,
        CreationDate DATETIME DEFAULT GETDATE(),
        Width INT,
        Height INT,
        Encode VARCHAR(MAX),
        PRIMARY KEY (Id),
        CONSTRAINT FK_Art_Artist FOREIGN KEY (ArtistId) REFERENCES Artist(Id) ON DELETE CASCADE
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Likes')
BEGIN
    CREATE TABLE Likes (
        ArtistId INT,
        ArtId INT,
        PRIMARY KEY (ArtistId, ArtId),
        CONSTRAINT FK_Likes_Artist FOREIGN KEY (ArtistId) REFERENCES Artist(Id),
        CONSTRAINT FK_Likes_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE
    );
END
GO
IF NOT EXISTS (SELECT * FROM sys.tables WHERE name = 'Comment')
BEGIN
    CREATE TABLE Comment (
        Id INT IDENTITY(1,1) NOT NULL,
        ArtistId INT,
        ArtId INT,
        ReplyId INT,
        [Message] VARCHAR(2222),
        CreationDate DATETIME DEFAULT GETDATE(),
        PRIMARY KEY (Id),
        CONSTRAINT FK_Comment_Artist FOREIGN KEY (ArtistId) REFERENCES Artist(Id),
        CONSTRAINT FK_Comment_Art FOREIGN KEY (ArtId) REFERENCES Art(Id) ON DELETE CASCADE,
        CONSTRAINT FK_Comment_Comment FOREIGN KEY (ReplyId) REFERENCES Comment(Id)
    );
END
