<script>
const percentWidthToPix = (percent, ctx) =>
  Math.floor((ctx.canvas.width / 100) * percent);
const percentHeightToPix = (percent, ctx) =>
  Math.floor((ctx.canvas.height / 100) * percent);

export default {
  inject: ['provider'],

  props: {
    imageCode: {
        type: String,
        default: ''
    },
    imageHeight: {
        type: Number,
        default: 0,
    },
    imageWidth: {
        type: Number,
        default : 0
    }
  },

  data() {
        return{
            testbox:{
                imageCode: '',
                imageHeight: 32,
                imageWidth: 32,

            },
        };
  },

  computed: {
    
  },

  // eslint-disable-next-line vue/require-render-return
  render() {
    if (!this.provider.context) return;
    const imageTest = '';
    var pixSize = 10;
    var indexcode = 0;
    var b = 0;
    var e = 6
    const ctx = this.provider.context;
     for(let column = 0; column < 32 * pixSize; column+= pixSize ){
         for(let row = 0; row < 32 * pixSize; row += pixSize){
             ctx.fillStyle ='#' + imageTest.substring(b,e);
             ctx.fillRect(column+380,row,pixSize,pixSize);
             b+= 6
             e+= 6
         }
        
     }

 
  },
};
</script>